mapper.select:
select 
from ORGTYPE
where STATUS!=2 and FK_PARENT=#{parentid,jdbcType=DECIMAL}

mapper.update:
update ORGTYPE
set NAME=#{name,jdbcType=VARCHAR},
	REMARK=#{remark,jdbcType=VARCHAR}, 
	LASTOPUSER=#{lastOpUser,jdbcType=VARCHAR},
	LASTOPDATE=#{lastOpDate,jdbcType=TIMESTAMP}
where ID=#{id,jdbcType=DECIMAL}

update ORGTYPE
set STATUS=#{status,jdbcType=DECIMAL},
	LASTOPUSER=#{lastOpUser,jdbcType=VARCHAR},
	LASTOPDATE=#{lastOpDate,jdbcType=TIMESTAMP}
where ID=#{id,jdbcType=DECIMAL}

update ORGTYPE
set STATUS=#{status,jdbcType=DECIMAL},
	LASTOPUSER=#{lastOpUser,jdbcType=VARCHAR},
	LASTOPDATE=#{lastOpDate,jdbcType=TIMESTAMP}
where FK_PARENT=#{id,jdbcType=DECIMAL}
